<?php//usleep(500000);// load information about seats$map = array();if (($handle = fopen('theatre.csv', 'r')) !== FALSE){    while (($data = fgetcsv($handle, 1000, ';')) !== FALSE)	{        $key = 'seat-'.$data[2].'-'.$data[3].'-'.$data[4];        $map[$key] = array(			'id'		=> $data[0],			'sector'	=> $data[1],			'sector_id'=> $data[2],			'row'		=> $data[3],			'number'	=> $data[4],			'price_full'	=> $data[5],			'price_reduced'	=> $data[6],			'status'	=> $data[7],			'selected'	=> $data[8]		);    }    fclose($handle);}echo json_encode($map);